
import ICommEmit = require('../eventing/ICommEmit');

interface IInterceptedAsk<answerType> {
    (   emit: ICommEmit,
        callback: (e: Error, response?: answerType) => void,
        next: (callback: (e: Error, answer?: answerType) => void) => void,
        stop: () => void
    ): void;
}

export = IInterceptedAsk;
