
import ICommEmit = require('../eventing/ICommEmit');

interface IInterceptedRequest<requestType, responseType> {
    (   emit: ICommEmit,
        request: requestType,
        callback: (e: Error, response?: responseType) => void,
        next: (request: requestType, callback: (e: Error, response?: responseType) => void) => void,
        stop: () => void
    ): void;
}

export = IInterceptedRequest;
